mapper.select:
select top (#{back}- #{front}+1) * from (select Row_Number() over ( order by TimeIn Desc ) as id , * from ( select a.InBillID, b.ItemsInID,a.InvoiceID, a.TimeIn,c.ItemsName,b.Type,b.StorePosition,b.ItemNum,b.ItemPrice,b.ItemTotal,a.AllTotal,d.UserName,a.CheckStatus,b.Note from InBill as a left join ItemsIn as b on a.InBillID = b.InBillID left join Goods as c on b.GoodsID=c.GoodsID left join [User] as d on d.UserID = a.Operator where 1=1
and TimeIN between #{time1} and #{time2}
and ItemsName like '%${itemName}%'
and InvoiceID like '%${Invoice}%'
and username like '%${username}%'
and CheckStatus = (#{checkStatus} - 1)
)as show)as show1 where id not in ( select top (#{front}-1) id from (select Row_Number() over ( order by getdate() ) as id , * from ( select a.InBillID, b.ItemsInID,a.InvoiceID, a.TimeIn,c.ItemsName,b.Type,b.StorePosition,b.ItemNum,b.ItemPrice,b.ItemTotal,a.AllTotal,d.UserName,a.CheckStatus,b.Note from InBill as a left join ItemsIn as b on a.InBillID = b.InBillID left join Goods as c on b.GoodsID=c.GoodsID left join [User] as d on d.UserID = a.Operator where 1 = 1
and TimeIN between #{time1} and #{time2}
and ItemsName like '%${itemName}%'
and InvoiceID like '%${Invoice}%'
and username like '%${username}%'
and CheckStatus = (#{checkStatus} - 1)
)as show)as show1 )
