mapper.select:
select count(*)
from [Role] where 1=1

    and RoleName = #{roleName}

mapper.update:
update Role
set RoleName = #{roleName}
where RoleID = #{RoleID}

mapper.delete:
delete from RolePermission
where 1= 1

    and RoleID = #{roleID}


    and PermissionID = #{permissionID}

mapper.select:
select * from
RolePermission
where 1=1

    and RoleID = #{roleID}


    and PermissionID = #{permissionID}
